    public static class GeneratedWord
    {
        protected String currentWord;
        protected boolean wordReal;
        protected String currentDefinition;

        public java.lang.String getCurrentWord() {
            return currentWord;
        }

        public void setCurrentWord(java.lang.String currentWord) {
            this.currentWord = currentWord;
        }

        public boolean isWordReal() {
            return wordReal;
        }

        public void setWordReal(boolean wordReal) {
            this.wordReal = wordReal;
        }
    }

    public void initWords()
    {
        allWords.replaceAll(String::toUpperCase);
        for(int i = 0; i < allWords.size(); i++){
            if(allWords.get(i).toUpperCase(Locale.ROOT).contains("A") || allWords.get(i).toUpperCase(Locale.ROOT).contains("E") || allWords.get(i).toUpperCase(Locale.ROOT).contains("I") || allWords.get(i).toUpperCase(Locale.ROOT).contains("O") || allWords.get(i).toUpperCase(Locale.ROOT).contains("U") || allWords.get(i).toUpperCase(Locale.ROOT).contains("Y")){
                vowelWords.add(allWords.get(i));
            }

        }
        for(int i = 0; i < realWords.size();i++){
            for(int j = 0; j < vowelWords.size();j++)
                if(realWords.get(i).compareTo(vowelWords.get(j)) == 0) {
                    key.add(j);
                }
        }

        for(int i = 0; i < vowelWords.size();i++) {
            for (int j = 0; j < realWords.size(); j++)
                if (realWords.get(j).compareTo(vowelWords.get(i)) != 0) {
                    amountNotEqual++;

                }
            if(amountNotEqual == realWords.size()){
                fakeWords.add(vowelWords.get(i));
            }
            amountNotEqual = 0;
        }
    }

    public GeneratedWord generateWord()
    {
        GeneratedWord genWord = new GeneratedWord();
        Random rand = new Random();
        String currentWord = new String();
        boolean isWordReal = false;
        String currentDefinition = new String();
        int binary = rand.nextInt(2);
        if(binary == 1){
            isWordReal = true;
            int int_random = rand.nextInt(realWords.size());
            currentWord = realWords.get(int_random);
            currentDefinition = definitions.get(int_random);
        }
        if(binary == 0){
            isWordReal = false;
            int int_random = rand.nextInt(fakeWords.size());
            currentWord = fakeWords.get(int_random);

        }
        genWord.currentWord=currentWord;
        genWord.wordReal=isWordReal;
        genWord.currentDefinition = currentDefinition;


        return genWord;
    }
